import{createSlice}from"@reduxjs/toolkit";import{v4 as uuidv4}from"uuid";export const AmountSlice=createSlice({name:"amounts",initialState:{value:[]},reducers:{addAmount:(e,a)=>{a.payload.amount>0&&(e.value=[...e.value,{userId:a.payload.user_id,amount:a.payload.amount,id:a.payload.id?a.payload.id:uuidv4(),description:a.payload.description,date:a.payload.date_create}])},editUser:(e,a)=>{e.value=e.value.map((e=>e.id===a.payload.id?{...e,amount:a.payload.amount,description:a.payload.description}:e))},removeAmount:(e,a)=>{e.value=e.value.filter((e=>e.id!==a.payload.id))},removeAllAmountsByUserId:(e,a)=>{e.value=e.value.filter((e=>e.userId!==a.payload))}}});export const{addAmount:addAmount,editUser:editUser,removeAmount:removeAmount,removeAllAmountsByUserId:removeAllAmountsByUserId}=AmountSlice.actions;export const selectAmount=e=>e.amounts.value;export default AmountSlice.reducer;