import{createSlice}from"@reduxjs/toolkit";import{v4 as uuidv4}from"uuid";export const AmountSlice=createSlice({name:"amounts",initialState:{value:[]},reducers:{addAmount:(e,o)=>{o.payload.amount>0&&(e.value=[...e.value,{userId:o.payload.user_id,amount:o.payload.amount,id:o.payload.id?o.payload.id:uuidv4(),description:o.payload.description}])},removeAmount:(e,o)=>{e.value=e.value.filter((e=>e.id!==o.payload.id))},removeAllAmountsByUserId:(e,o)=>{e.value=e.value.filter((e=>e.userId!==o.payload))}}});export const{addAmount:addAmount,removeAmount:removeAmount,removeAllAmountsByUserId:removeAllAmountsByUserId}=AmountSlice.actions;export const selectAmount=e=>e.amounts.value;export default AmountSlice.reducer;