import{createSlice}from"@reduxjs/toolkit";import{v4 as uuidv4}from"uuid";export const UsersSlice=createSlice({name:"users",initialState:{value:[]},reducers:{addUser:(e,a)=>{a.payload.name&&(e.value=[...e.value,{id:a.payload.id?a.payload.id:uuidv4(),name:a.payload.name,status:a.payload.status,date_create:a.payload.date_create,date_change:a.payload.date_change,date_delete:a.payload.date_delete}])},editUser:(e,a)=>{e.value=e.value.map((e=>e.id===a.payload.id?{...e,name:a.payload.name,status:a.payload.status,date_delete:a.payload.date_delete,date_change:a.payload.date_change}:e))},removeUser:(e,a)=>{e.value=e.value.map((e=>e.id===a.payload.id?{...e,date_delete:a.payload.date_delete,status:1}:e))}}});export const{addUser:addUser,editUser:editUser,removeUser:removeUser}=UsersSlice.actions;export const selectUsers=e=>e.users.value;export const selectUsersNumber=e=>e.users.value.length;export default UsersSlice.reducer;