const db=require("./db"),config=require("../config");async function get(){return await db.query("SELECT * FROM finance_amounts")||[]}async function add({description:n,user_id:e,amount:t}){return await db.query(`INSERT INTO finance_amounts(user_id, amount, description, status, date_create, date_change, date_delete)\n    VALUES(${e}, ${t}, '${n}', '0', now(), '0000-00-00 00:00:00', '0000-00-00 00:00:00');`)}async function change({id:n,status:e,amount:t,description:a}){return await db.query(`UPDATE finance_amounts SET\n      ${t?`amount='${t}',`:""}\n      ${a?`description='${a},'`:""}\n      status = '${e||0}',\n      date_delete = ${1===e?"now(),":"'0000-00-00 00:00:00',"}\n      ${1===e?"":"date_change = now()"}\n      WHERE id = '${n}';`)}module.exports={get:get,add:add,change:change};