import{createSlice}from"@reduxjs/toolkit";import{v4 as uuidv4}from"uuid";export const AmountSlice=createSlice({name:"amounts",initialState:{value:[]},reducers:{addAmount:(a,e)=>{e.payload.amount>0&&(a.value=[...a.value,{id:e.payload.id?e.payload.id:uuidv4(),userId:e.payload.user_id,amount:e.payload.amount,description:e.payload.description,status:e.payload.status,date_create:e.payload.date_create,date_change:e.payload.date_change,date_delete:e.payload.date_delete}])},editAmount:(a,e)=>{a.value=a.value.map((a=>a.id===e.payload.id?{...a,amount:e.payload.amount,description:e.payload.description,status:e.payload.status,date_delete:e.payload.date_delete,date_change:e.payload.date_change}:a))},removeAmount:(a,e)=>{a.value=a.value.map((a=>a.id===e.payload.id?{...a,date_delete:e.payload.date_delete,status:1}:a))}}});export const{addAmount:addAmount,editAmount:editAmount,removeAmount:removeAmount}=AmountSlice.actions;export const selectAmount=a=>a.amounts.value;export default AmountSlice.reducer;