import{createSlice}from"@reduxjs/toolkit";import{v4 as uuidv4}from"uuid";export const TargetsSlice=createSlice({name:"targets",initialState:{value:[]},reducers:{addTarget:(e,a)=>{a.payload.name&&(e.value=[...e.value,{id:a.payload.id?a.payload.id:uuidv4(),name:a.payload.name,status:a.payload.status,date_create:a.payload.date_create,date_change:a.payload.date_change,date_delete:a.payload.date_delete}])},editTargetr:(e,a)=>{e.value=e.value.map((e=>e.id===a.payload.id?{...e,name:a.payload.name,status:a.payload.status,date_delete:a.payload.date_delete,date_change:a.payload.date_change}:e))},removeTarget:(e,a)=>{e.value=e.value.map((e=>e.id===a.payload.id?{...e,date_delete:a.payload.date_delete,status:1}:e))}}});export const{addTarget:addTarget,editTarget:editTarget,removeTargetr:removeTargetr}=TargetsSlice.actions;export const selectTargets=e=>e.targets.value;export const selectTargetsNumber=e=>e.targets.value.length;export default TargetsSlice.reducer;